void hwid_Init(void)
{
    // Initialize values
    hwid_ADC_value = 0;
    hwid_value = 0;

    // Read hardware ID from ADC
    hwid_ReadADCValue();

    // Calculate Hardware version
    hwid_value = hwid_CalculateHardwareIDValue(hwid_ADC_value);
} // end hwid_Init()

void hwid_ReadADCValue(void)
{
    hwid_ADC_value = adc__get__hardware_id_value();
} // end hwid_ReadADCValue()

uint8_t hwid_CalculateHardwareIDValue(uint16_t hwid_ADC_value)
{
    return hwid_MapADCToRevision[hwid_ADC_value];
} // end hwid_CalculateHardwareIDValue()

uint16_t hwid_GetHardwareIDADCValue(void)
{
    return hwid_ADC_value;
} // end hwid_GetHardwareIDADCValue()

uint8_t hwid_GetHardwareIDValue(void)
{
    return hwid_value;
} // end hwid_GetHardwareIDValue()

